mapper.select:
select t1.user_id, t1.name, t1.age, t1.gender, t1.phone, t1.email, t1.health, t1.user_name, t1.password,
t1.status, t1.rule_id, t1.remark, t1.create_time, t1.update_time, t2.diagnosis, t2.feedback, t2.user_id,
t2.cases_url, t2.cases_img_url
from user_message as t1
right join cases as t2 on t1.user_id = t2.user_id

    and status = #{status}
    and t1.name like concat('%',#{name},'%')
    and t1.phone like concat('%',#{phone},'%')
    and t1.health = #{health}


    limit #{startSize}, #{pageSize}

select t1.user_id, t1.name, t1.age, t1.gender, t1.phone, t1.email, t1.health, t1.user_name, t1.password, t1.status, t1.rule_id, t1.remark, t1.create_time, t1.update_time, t2.diagnosis, t2.feedback, t2.cases_url, t2.cases_img_url from user_message as t1 right join cases as t2 on t1.user_id = t2.user_id
and t2.user_id in
#{userId}
