mapper.select:
select user_id, name, age, gender, phone, email, health, user_name, password, status, rule_id, remark, create_time, update_time from user_message
and status = 1 and name like concat('%',#{name},'%'} and phone like concat('%',#{phone},'%') and email like concat('%',#{email},'%')
and age between #{minAge} and #{maxAge}
and date_format(createTime,'%y%m%d%h%i%s') between date_format(#{minCreatetime},'%y%m%d%h%i%s') and date_format(#{maxCreatetime},'%y%m%d%h%i%s')
limit #{currentPage}, #{pageSize}
